mapper.select:
select roomId, contentId, senderId, receiverId, createdAt, recentMessage
from chatrooms
where senderId=#{userId} or receiverId=#{userId}
order by createdAt desc;

select roomId, contentId, senderId, receiverId, createdAt, recentMessage
from chatrooms
where roomId = #{roomId}

select chatId, roomId, userId, content, createdAt, isRead
from chats
where roomId = #{roomId}
order by createdAt;

mapper.update:
UPDATE chatrooms 
SET senderId = #{senderId}, receiverId = #{receiverId}, recentMessage = #{recentMessage}
WHERE roomId = #{roomId}

UPDATE chatrooms 
SET recentMessage = #{message} 
WHERE roomId = #{roomId}

UPDATE chats 
SET isRead = 1 
WHERE roomId = #{roomId}
