mapper.select:
select userName, userId, userPw, email, createdAt
from members
where userId = #{userId}

mapper.update:
update members
set userName = #{userName}, userPw = #{userPw}, email = #{email}
where userId = #{userId}

mapper.select:
select userPw
from members
where userId= #{userId}

select userId, userName, token
from members
where userId = #{userId} and userPw = #{userPw}

select userName, userId, email
from members
where userId = #{userId}

mapper.update:
update members
set token = #{token}
where userId = #{userId}

mapper.select:
select token
from members
where userId = #{userId}

mapper.update:
update members
set token = #{token, jdbcType=VARCHAR}
where userId = #{userId}
