mapper.select:
select userName, userId, userPw, email, createdAt
from members
where userId = #{userId}

mapper.update:
update members
set userName = #{userName}, userPw = #{userPw}, email = #{email}
where userId = #{userId}

mapper.select:
	  	select userName, userId, userPw, email, createdAt
 		from members
 		where userId = #{userId}
 		and userPw = #{userPw}

select userPw
from members
where userId= #{userId}
